import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/main.css'; // main.css 파일 import

function Main() {
  const [posts, setPosts] = useState([]);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  
  useEffect(() => {
    // 포스트 목록을 가져오는 로직
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/posts');
        setPosts(response.data.posts);
      } catch (error) {
        console.error(error);
      }
    };

    // 현재 로그인된 사용자의 정보를 가져오는 로직
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/user'); // 이 부분은 로그인 상태에 따라 변경되어야 합니다.
        setUsername(response.data.username);
      } catch (error) {
        console.error(error);
      }
    };

    fetchPosts();
    fetchUserData();
  }, []); // 빈 배열을 전달하여 한 번만 실행되도록 함

  return (
    <div className="main-container">
      <div className="post-form-container">
        {/* 포스트 작성 폼 */}
        <textarea placeholder={`안녕하세요, ${username}! 어떤 생각을 하고 계신가요?`} />
        <button>포스트</button>
      </div>

      <div className="post-list-container">
        {/* 포스트 목록 */}
        {posts.map((post) => (
          <div key={post.id} className="post">
            <p>{post.content}</p>
            <span>작성자: {post.username}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Main;
